

UPDATE `RA_TNT_APP_PARAMS` SET `ATTR_VALUE`='SELECT B.LOAN_NUM AS LOANNUMBER, A.PROPSTRNBR, A.PROPSTREET, A.PROPCITY, A.STATE, A.PROPZIP, A.PROPTYPE, A.ORGPRINBAL, A.CURPRINBAL, A.PIPMTAMT, A.ESCROWPMT, A.OCCPTYPE, A.FAIRMKTVAL, A.CREDITSCORE, A.PURCHASEPRICE, A.CREDITSCOREDT, A.ORGAPPRVAL, B.REO_DT AS REO_DATE, B.UPB  AS UPB_RES FROM ARLT_ARLT_MORTGAGE1 A RIGHT OUTER JOIN SHAHMAYU.REO_PORTFOLIO_MASTER B ON A.LOANNUMBER = B.LOAN_NUM WHERE B.LOAN_NUM = ? ORDER BY B.AS_OF_DT DESC FETCH FIRST 1 ROW ONLY' WHERE  `ATTR_KEY`='RR_QUERY';